var assert=require("assert");
var pdfCom=require("../libs/pdfthumbnail");
var fs=require("fs");
describe("PDFThumbnail component",function(){
    

    it (" should generate an image if pdf has 1 page",function(done){
        pdfCom.generateFromFilePath(__dirname+"/test1.pdf",function(err,filePath){
            assert(!err);
            assert(fs.existsSync(filePath));
            done();
        });
    });

it (" should generate an image if pdf has multiple page",function(done){
        pdfCom.generateFromFilePath(__dirname+"/test.pdf",function(err,filePath){
            assert(!err);
            console.log(filePath);
            assert(fs.existsSync(filePath));
            done();
        });
    });
    it (" should generate an image to target path",function(done){
        pdfCom.generateFromFilePath(__dirname+"/test.pdf","/tmp/test.png",function(err,filePath){
            assert(!err);
            assert(fs.existsSync("/tmp/test.png"));
            done();
        });
    });
});